import React from "react";
import ReactDOM from "react-dom";
import TemplateMail from "../template/TemplateMail";
class ModalShow extends React.Component {
  //fisrt arg is jsx
  //second arg is ref to html

  render() {
    return ReactDOM.createPortal(
      <div
        onClick={this.props.onDismiss}
        className="ui dimmer modals visible active"
      >
        <div
          onClick={(event) => event.stopPropagation()}
          className="ui standart modal visible active "
        >
          <div className="header">{this.props.title}</div>
          <div className="actions" style={{ textAlign: "left", padding: "0" }}>
            <TemplateMail initialValues={{ subject: this.props.id }} />
            );
          </div>
        </div>
      </div>,
      document.querySelector("#modal")
    );
  }
}

export default ModalShow;
