import React from "react";
import { connect } from "react-redux";
import { fetchAllTemplates, useTemplate, deleteTemplate } from "../../actions";
import { Link } from "react-router-dom";

class TemplateList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { askDelete: false, idToDelete: null };
  }

  componentDidMount() {
    this.props.fetchAllTemplates();
  }

  renderCard() {
    return this.props.templates.map((template) => {
      return (
        <div key={template._id} className="card orange">
          <div className="content">
            <div className="header">{template.templateName}</div>
            <div className="description">{template.subject}</div>
          </div>
          <Link
            to={`/templates/use/${template._id}`}
            className="ui bottom attached button"
          >
            <i className="add icon"></i>
            Use Template
          </Link>
          <Link
            to={`/templates/edit/${template._id}`}
            className="ui bottom attached button"
          >
            <i className="edit icon"></i>
            Edit Template
          </Link>
          <div
            className={`ui ${
              this.state.idToDelete === template._id ? "red " : ""
            }bottom attached button`}
            onClick={() => {
              if (this.state.idToDelete === true) {
                this.props.deleteTemplate(template._id);
                this.setState({ idToDelete: null });
              } else {
                this.setState({ idToDelete: template._id });
              }
            }}
          >
            <i className="remove icon"></i>
            Delete Template
          </div>
        </div>
      );
    });
  }
  render() {
    console.log(this.state.askDelete);
    console.log(this.state.idToDelete);
    return <div className="ui  four cards">{this.renderCard()}</div>;
  }
}

const matStateToProps = (state = {}) => {
  return {
    templates: Object.values(state.templates),
    mail: state.mail
  };
};

export default connect(
  matStateToProps,
  { fetchAllTemplates, useTemplate, deleteTemplate }
)(TemplateList);
