import React from "react";
import history from "../../history";
import ModalShow from "../modals/ModalShow";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { fetchSingleTemplate } from "../../actions";

class TemplateShow extends React.Component {
  componentDidMount() {
    this.props.fetchSingleTemplate(this.props.match.params.id);
  }
  renderActions() {
    return (
      <React.Fragment>
        <button
          // bu sekilde invo etmeden parametresi haz覺r fonksiyon cevirmis olduk
          className="ui button negative"
        >
          Delete
        </button>
        <Link to="/" className="ui button">
          Cancel
        </Link>
      </React.Fragment>
    );
  }
  renderContent() {
    if (!this.props.template) {
      return "loading";
    }
    return <div>{this.props.template.subject}</div>;
  }
  render() {
    console.log("buras覺 Templateshow");
    console.log(this.props.template);
    return (
      <ModalShow
        onDismiss={() => {
          history.push("/");
        }}
        title="Delete Stream"
        content={this.renderContent()}
        actions={this.renderActions()}
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    template: state.templates[ownProps.match.params.id]
  };
};
export default connect(
  mapStateToProps,
  { fetchSingleTemplate }
)(TemplateShow);
