import React from "react";
import history from "../../history";
import ModalShow from "../modals/ModalShow";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { fetchSingleTemplate } from "../../actions";
import TemplateMail from "./TemplateMail";
import _ from "lodash";

class TemplateShow extends React.Component {
  componentWillMount() {
    console.log("component");
    this.props.fetchSingleTemplate(this.props.match.params.id);
  }

  render() {
    console.log("render");
    return (
      <ModalShow
        onDismiss={() => {
          history.push("/");
        }}
        title="Mail"
        id={this.props.template}
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log("mapstatetoprops");
  return {
    template: state.templates[ownProps.match.params.id]
  };
};
export default connect(
  mapStateToProps,
  { fetchSingleTemplate }
)(TemplateShow);
