import React from "react";
import { Field, reduxForm } from "redux-form"; // field is component , reduxForm is connnect function that we used in redux
import { Link } from "react-router-dom";
import { connect } from "react-redux";
class TemplateMail extends React.Component {
  //buraya meta yı gönderdik

  renderError({ error, touched }) {
    //check error
    if (touched && error) {
      return (
        <div className="ui error message">
          <div className="header">{error}</div>
        </div>
      );
    }
  }
  renderInput = ({ input, label, meta }) => {
    const classNameError = `field ${meta.error && meta.touched ? "error" : ""}`; // field ı kırmızıya boyamak
    return (
      <div className={classNameError}>
        <label className="ui label">{label}</label>
        <input {...input} autoComplete="off" />
        {this.renderError(meta)}
      </div>
    ); // input objesinin tamamını compenenetin icine esliyoruz example: onChange = fromPorps.input.onChange gibi
  };

  renderInputArea = ({ input, label, meta }) => {
    const classNameError = `field ${meta.error && meta.touched ? "error" : ""}`; // field ı kırmızıya boyamak
    return (
      <div className={classNameError}>
        <label className="ui label">{label}</label>

        <textarea {...input}></textarea>
        {this.renderError(meta)}
      </div>
    ); // input objesinin tamamını compenenetin icine esliyoruz example: onChange = fromPorps.input.onChange gibi
  };

  renderInputColor = ({ input, label, meta }) => {
    return (
      <div className="field">
        <label className="ui label">{label}</label>
        <select {...input}>
          className="ui fluid dropdown">
          <option value="red">Red</option>
          <option value="green">Green</option>
          <option value="blue">Blue</option>
        </select>
        {this.renderError(meta)}
      </div>
    ); // input objesinin tamamını compenenetin icine esliyoruz example: onChange = fromPorps.input.onChange gibi
  };

  onSubmit = (formValues) => {
    this.props.onSubmit(formValues);
  };

  render() {
    //console.log(this.props);
    return (
      <div className=" ui grid">
        {this.props.mail}
        <div className="column">
          <form
            className="ui form error"
            onSubmit={this.props.handleSubmit(this.onSubmit)}
          >
            <Field
              name="recipients"
              component={this.renderInput}
              label="Recipients"
            />
            <Field
              name="subject"
              component={this.renderInput}
              label="Subject"
            />
            <Field
              name="context"
              component={this.renderInputArea}
              label="Context"
            />
            <button className="ui button primary" style={{ marginTop: "10px" }}>
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

const validate = (formValues) => {
  const errors = {};
  if (!formValues.title) {
    errors.title = " You must enter a title";
  }
  if (!formValues.description) {
    errors.description = "You must enter a description";
  }
  return errors;
};

const mapStateToProps = (state) => {
  return {
    mail: state.mail
  };
};

const fromWrapped = reduxForm({
  // burayı refactor'lüyoruz cünkü connect i kullanmamız lazım
  // normal connect fonksiyonun aksine tek parametre alıyor ve bu obje bir configration objesi
  form: "mailForm",
  validate: validate //sadece validate de yazılabilir
})(TemplateMail);

export default connect(mapStateToProps)(fromWrapped);
