import React from "react";
import { connect } from "react-redux";
import { fetchAllTemplates, useTemplate } from "../../actions";

class TemplateList extends React.Component {
  componentDidMount() {
    this.props.fetchAllTemplates();
  }

  renderCard() {
    return this.props.templates.map((template) => {
      return (
        <div key={template._id} className="card orange">
          <div className="content">
            <div className="header">{template.subject}</div>
            <div className="description">{template.context}</div>
          </div>
          <div
            onClick={this.onClickHandler}
            className="ui bottom attached button"
          >
            <i className="add icon"></i>
            Use Template
          </div>
          <div className="ui bottom attached button">
            <i className="edit icon"></i>
            Edit Template
          </div>
          <div className="ui bottom attached button">
            <i className="remove icon"></i>
            Delete Template
          </div>
        </div>
      );
    });
  }
  render() {
    return <div className="ui cards">{this.renderCard()}</div>;
  }
}

const matStateToProps = (state = {}) => {
  return {
    templates: Object.values(state.templates)
  };
};

export default connect(
  matStateToProps,
  { fetchAllTemplates, useTemplate }
)(TemplateList);
