import React from "react";
import Modal from "../Modal";
import history from "../../history";

import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { fetchSingleTemplate } from "../../actions";

class TemplateShow extends React.Component {
  componentDidMount() {
    this.props.fetchSingleTemplate(this.props.match.params.id);
  }
  renderActions() {
    return (
      // jsx returnlerinde simdiye kadar hep elementleri bir div in icinde wrapladık ve gönderdim fakat eger
      // wraplamamız gereken bir durum oldugunda React.Fragment nesnesini kullanıyoruz ekrana renderlarnırken hicbir
      // html elementi olusturmuyor

      <React.Fragment>
        <button
          // bu sekilde invo etmeden parametresi hazır fonksiyon cevirmis olduk
          className="ui button negative"
        >
          Delete
        </button>
        <Link to="/" className="ui button">
          Cancel
        </Link>
      </React.Fragment>
    );
  }
  renderContent() {
    if (!this.props.template) {
      return "Are you sure you want to delete the template ?";
    }
    return `Are you sure you want to delete the template named:  ${this.props.template.subject}`;
  }
  render() {
    return (
      <Modal
        onDismiss={() => {
          history.push("/");
        }}
        title="Delete Stream"
        content={this.renderContent()}
        actions={this.renderActions()}
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    template: state.templates[ownProps.match.params.id]
  };
};
export default connect(
  mapStateToProps,
  { fetchSingleTemplate }
)(TemplateShow);
