import React from "react";
import history from "../../history";
import ModalShow from "../modals/ModalShow";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { fetchSingleTemplate } from "../../actions";
import TemplateMail from "./TemplateMail";
import _ from "lodash";

class TemplateShow extends React.Component {
  componentDidMount() {
    this.props.fetchSingleTemplate(this.props.match.params.id);
  }
  renderActions() {
    console.log(this.props.template);
    return (
      <React.Fragment>
        <TemplateMail initialValues={{ subject: this.props.template._id }} />
      </React.Fragment>
    );
  }
  renderContent() {
    if (!this.props.template) {
      return "loading";
    }
    return <div>{this.props.template.subject}</div>;
  }
  render() {
    this.props.fetchSingleTemplate(this.props.match.params.id);
    console.log("burası Templateshow");
    console.log(this.props.template);
    return (
      <ModalShow
        onDismiss={() => {
          history.push("/");
        }}
        title="Mail"
        content={this.renderContent()}
        actions={this.renderActions()}
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    template: state.templates[ownProps.match.params.id]
  };
};
export default connect(
  mapStateToProps,
  { fetchSingleTemplate }
)(TemplateShow);
